import { useState } from "react";
import Login from "./components/Login";
import Dashboard from "./components/Dashboard";
import Register from "./components/Register";
import Attendance from "./components/Attendance";
import Payments from "./components/Payments";
import Charts from "./components/Charts";
import Trainers from "./components/Trainers";
import "./App.css";


function App() {
  const [page, setPage] = useState("login");
  const [role, setRole] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) setPage("dashboard");
  }, []);


  return (
    <div className="container">
      <h1>üèãÔ∏è Gym Management System</h1>

      <nav>
        <button onClick={() => setPage("login")}>Login</button>
        <button onClick={() => setPage("register")}>Register</button>
        <button onClick={() => setPage("dashboard")}>Dashboard</button>
        <button onClick={() => setPage("attendance")}>Attendance</button>
        <button onClick={() => setPage("payments")}>Payments</button>
        <button onClick={() => setPage("charts")}>Charts</button>
        <button onClick={() => setPage("trainers")}>Trainers</button>
      </nav>

      {page === "login" && <Login setPage={setPage} setRole={setRole}/>}
      {page === "register" && <Register />}
      {page === "dashboard" && <Dashboard  role={role}/>}
      {page === "attendance" && <Attendance />}
      {page === "payments" && <Payments />}
      {page === "charts" && <Charts />}
      {page === "trainers" && <Trainers />}
    </div>
  );
}

export default App;
