import axios from "axios";
import { useEffect, useState } from "react";

const API = process.env.REACT_APP_API_URL || "http://127.0.0.1:8000";

function Trainers({ role }) {
  const [trainers, setTrainers] = useState([]);
  const [name, setName] = useState("");
  const [specialization, setSpecialization] = useState("");

  // Fetch all trainers
  const fetchTrainers = async () => {
    try {
      const res = await axios.get(`${API}/trainers`);
      setTrainers(res.data);
    } catch (err) {
      console.error("Failed to fetch trainers", err);
    }
  };

  useEffect(() => {
    fetchTrainers();
  }, []);

  // Add new trainer (admin only)
  const addTrainer = async () => {
    if (!name || !specialization) {
      alert("Please fill all fields");
      return;
    }
    try {
      await axios.post(`${API}/trainers`, { name, specialization });
      alert("Trainer added successfully");
      setName("");
      setSpecialization("");
      fetchTrainers(); // Refresh list
    } catch (err) {
      console.error("Failed to add trainer", err);
      alert("Failed to add trainer");
    }
  };

  // Delete trainer (admin only)
  const deleteTrainer = async (id) => {
    try {
      await axios.delete(`${API}/trainers/${id}`);
      alert("Trainer removed");
      fetchTrainers();
    } catch (err) {
      console.error("Failed to delete trainer", err);
      alert("Failed to delete trainer");
    }
  };

  return (
    <>
      <h2>Trainers</h2>

      {role === "admin" && (
        <div className="add-trainer">
          <h3>Add Trainer</h3>
          <input
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            placeholder="Specialization"
            value={specialization}
            onChange={(e) => setSpecialization(e.target.value)}
          />
          <button onClick={addTrainer}>Add Trainer</button>
        </div>
      )}

      <div className="trainer-list">
        {trainers.map((t) => (
          <div key={t.id} className="trainer-item">
            <p>
              {t.name} - {t.specialization}
            </p>
            {role === "admin" && (
              <button onClick={() => deleteTrainer(t.id)}>Delete</button>
            )}
          </div>
        ))}
      </div>
    </>
  );
}

export default Trainers;
