import axios from "axios";
import { useState, useEffect } from "react";

const API = process.env.REACT_APP_API_URL || "http://localhost:8000";

function Payments({ username }) {
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState("");
  const [payments, setPayments] = useState([]);

  // Fetch payments for current user
  const fetchPayments = async () => {
    try {
      const res = await axios.get(`${API}/payments`, {
        params: { username }  // only this user's payments
      });
      setPayments(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // Make payment
  const makePayment = async () => {
    if (!amount || !date) return alert("Enter amount and date");

    try {
      const res = await axios.post(`${API}/payments`, {
        username,  // logged-in user
        amount: parseFloat(amount),
        date
      });

      alert(res.data.msg);

      // clear input
      setAmount("");
      setDate("");

      // refresh payment list
      fetchPayments();
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.detail || "Payment failed");
    }
  };

  useEffect(() => {
    if (username) fetchPayments();
  }, [username]);

  return (
    <div className="section-box">
      <h2>Payments for {username}</h2>

      <input
        placeholder="Amount"
        value={amount}
        onChange={e => setAmount(e.target.value)}
      />

      <input
        type="date"
        value={date}
        onChange={e => setDate(e.target.value)}
      />

      <button onClick={makePayment}>Pay</button>

      <h3>Payment History</h3>
      <ul>
        {payments.length === 0 && <li>No payments found</li>}
        {payments.map(p => (
          <li key={p.id}>
            â‚¹{p.amount} - {p.date}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Payments;
