import axios from "axios";
import { useEffect, useState } from "react";

const API = process.env.REACT_APP_API_URL || "http://127.0.0.1:8000";

function Attendance({ username }) {
  const [attendance, setAttendance] = useState([]);
  const [date, setDate] = useState("");

  const fetchAttendance = async () => {
    const res = await axios.get(`${API}/attendance`, { params: { username } });
    setAttendance(res.data);
  };

  useEffect(() => {
    if (username) fetchAttendance();
  }, [username]);

  const markAttendance = async () => {
    await axios.post(`${API}/attendance`, { username, date, status: "present" });
    alert("Attendance marked!");
    setDate("");
    fetchAttendance();
  };

  return (
    <div>
      <h2>Attendance for {username}</h2>
      <input type="date" value={date} onChange={e => setDate(e.target.value)} />
      <button onClick={markAttendance}>Mark Attendance</button>

      <ul>
        {attendance.map(a => (
          <li key={a.id}>{a.date} - {a.status}</li>
        ))}
      </ul>
    </div>
  );
}

export default Attendance;
