import axios from "axios";
import { useState, useEffect } from "react";

const API = process.env.REACT_APP_API_URL || "http://127.0.0.1:8000";

function Payments({ username }) {
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState("");
  const [payments, setPayments] = useState([]);

  // Fetch payments for logged-in user
  const fetchPayments = async () => {
    try {
      const res = await axios.get(`${API}/payments`, {
        params: { username }  // filter by current user
      });
      setPayments(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const makePayment = async () => {
    if (!amount || !date) {
      alert("Please enter amount and date");
      return;
    }

    try {
      const res = await axios.post(`${API}/payments`, {
        username,
        amount: parseFloat(amount),
        date
      });

      alert(res.data.msg);
      setAmount("");
      setDate("");
      fetchPayments();  // refresh list
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.detail || "Error making payment");
    }
  };

  useEffect(() => {
    if (username) fetchPayments();
  }, [username]);

  return (
    <div className="section-box">
      <h2>Payments for {username}</h2>

      <input
        placeholder="Amount"
        type="number"
        value={amount}
        onChange={e => setAmount(e.target.value)}
      />

      <input
        type="date"
        value={date}
        onChange={e => setDate(e.target.value)}
      />

      <button onClick={makePayment}>Pay</button>

      <h3>Payment Records</h3>
      <ul>
        {payments.length === 0 && <li>No payments found</li>}
        {payments.map(p => (
          <li key={p.id}>
            {p.date} - â‚¹{p.amount}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Payments;
